import React from 'react'
import allblog from '../blogdata/blog.json'
import { useParams } from 'react-router'

export default BlogDetailsPage = (props) => {
    var { slug } = useParams(),
        post = findPostBySlug(slug);
    return (
        <div>
            <div className="post-content-view">
                <h1 className="title">{post.blogTitle}</h1>
                <content>{post.blogText}</content>
            </div>
        </div>
    )


}




function findPostBySlug(slug) {
    return allblog.data.find(o => o.slug === slug);
}
