import React from 'react'
import allblog from '../blogdata/blog.json'

const AllPosts = () => {
    //console.log(allblog)
    return (
        <div>
            {
                allblog.data.map((items, i) => {
                    return (
                        <>

                            <img  alt="" srcset="" />
                            <div class="box alt">
                                <div class="row gtr-50 gtr-uniform">
                                    <div class="col-4">
                                    <span class="image fit"><img src={items.blogImage} alt="" /></span>
                                    <h3>{items.blogTitle}</h3>
                                    <h5>{items.blogCategory}</h5>
                                    <h5>{items.postedOn}</h5>
                                    <h5>{items.author}</h5>
                                    </div>

                                </div>
                            </div>
                        </>
                    )
                })
            }
        </div>
    )
}

export default AllPosts
