import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import { Link } from 'react-router-dom'
import HomeTextBanner from './HomeTextBanner'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
//import ReactDOM from 'react-dom';
//import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
//  import { Carousel } from 'react-responsive-carousel';


const InnerMenu = () => {
    var settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
    };
    const responsive = {
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3,
            paritialVisibilityGutter: 60
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2,
            paritialVisibilityGutter: 50
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1,
            paritialVisibilityGutter: 30
        }
    };

    const images = [
        "https://images.unsplash.com/photo-1549989476-69a92fa57c36",
        // "https://images.unsplash.com/photo-1549396535-c11d5c55b9df",
        // "https://images.unsplash.com/photo-1550133730-695473e544be",
        // "https://images.unsplash.com/photo-1550167164-1b67c2be3973",
        "https://images.unsplash.com/photo-1550338861-b7cfeaf8ffd8",
        // "https://images.unsplash.com/photo-1550223640-23097fc71cb2",
        // "https://images.unsplash.com/photo-1550353175-a3611868086b",
        // "https://images.unsplash.com/photo-1550330039-a54e15ed9d33",
        "https://images.unsplash.com/photo-1549737328-8b9f3252b927",
        "https://images.unsplash.com/photo-1549833284-6a7df91c1f65",
        "https://images.unsplash.com/photo-1549985908-597a09ef0a7c",
        "https://images.unsplash.com/photo-1550064824-8f993041ffd3"
    ];



    return (
        <div>
            <div id="main">

                {/* <!-- One --> */}
                <section id="one" className="tiles">
                    <article>
                        <span className="image">
                            <img src="images/pic01.jpg" alt="" />
                        </span>
                        <header className="major">
                            <h3><Link to="/portfolio" className="link animate__animated animate__fadeInUp">Customer First Experience</Link></h3>
                            <p className="animate__animated animate__slideInDown">In the end, we want a business, which driven by customers.</p>
                        </header>
                    </article>
                    <article>
                        <span className="image">
                            <img src="images/pic02.jpg" alt="" />
                        </span>
                        <header className="major">
                            <h3><Link to="/portfolio" className="link animate__animated animate__fadeInUp">Ingenious innovation</Link></h3>
                            <p className="animate__animated animate__slideInDown">We constantly experiment and evaluate the headless commerce</p>
                        </header>
                    </article>
                    <article>
                        <span className="image">
                            <img src="images/pic03.jpg" alt="" />
                        </span>
                        <header className="major">
                            <h3><Link to="/portfolio" className="link animate__animated animate__fadeInUp">Agility and speed</Link></h3>
                            <p className="animate__animated animate__slideInDown">We believe in the world of eCommerce "speed" defines the winner.</p>
                        </header>
                    </article>
                    <article>
                        <span className="image">
                            <img src="images/pic04.jpg" alt="" />
                        </span>
                        <header className="major">
                            <h3><Link to="/portfolio" className="link animate__animated animate__fadeInUp">Experience worth a decade</Link></h3>
                            <p className="animate__animated animate__slideInDown">Our team of experts have experience in their respective fields</p>
                        </header>
                    </article>
                    <article>
                        <span className="image">
                            <img src="images/pic05.jpg" alt="" />
                        </span>
                        <header className="major">
                            <h3><Link to="/portfolio" className="link animate__animated animate__fadeInUp">eCommerce agency</Link></h3>
                            <p className="animate__animated animate__slideInDown">We build customer-first digital commerce experience for international brands</p>
                        </header>
                    </article>
                    <article>
                        <span className="image">
                            <img src="images/pic06.jpg" alt="" />
                        </span>
                        <header className="major">
                            <h3><Link to="/portfolio" className="link animate__animated animate__fadeInUp">Less friction, More sell</Link></h3>
                            <p className="animate__animated animate__slideInDown">We help to increasing eCommerce store conversion rates.</p>
                        </header>
                    </article>
                </section>

                {/* <!-- Two --> */}
                <HomeTextBanner />
                <div id="wrapper">
                    <Carousel showArrows={true} className="fullwidthslider">
                        {/* {images.slice(0, 5).map(image => { */}
                        {images.map((image, key) => {
                            return (
                                <>
                                    <img draggable={false} style={{ width: "100%", height: "100%" }} src={image} />
                                    <p>{key}</p>
                                </>
                            );
                        })}
                    </Carousel>
                    <Slider {...settings}>
                        <div>
                            <h3>1</h3>
                        </div>
                        <div>
                            <h3>2</h3>
                        </div>
                        <div>
                            <h3>3</h3>
                        </div>
                        <div>
                            <h3>4</h3>
                        </div>
                        <div>
                            <h3>5</h3>
                        </div>
                        <div>
                            <h3>6</h3>
                        </div>
                    </Slider>
                </div>


                {/* <AwesomeSlider animation="cubeAnimation" play={true}  interval={1000}>
                    {images.slice(0, 5).map(image => {
                        return (
                            <img draggable={false} style={{ width: "100%", height: "100%" }} src={image} />
                            <p className="legend">Legend 1</p>
                        );
                    })}
                </AwesomeSlider> */}


                {/* <Carousel show={3.5} slide={2} transition={0.5}>
                    <div color="#f27a1a"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-3.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-6.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-1.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-2.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-3.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-4.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-5.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-7.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-8.png" alt="" /></span></div></div>
                    <div color="#d53f8c"><div class="col-4"><span class="image fit"><img src="https://scale-team.com/images/client-9.png" alt="" /></span></div></div>
                </Carousel> */}
            </div>
        </div>
